#!/bin/bash
# Azure Vision API Test Script

echo "üîç Testing Azure Vision API Connection"
echo "====================================="

# Load environment variables from .env
if [ -f ".env" ]; then
    echo "‚úÖ Loading environment variables from .env"
    # Handle both Unix (LF) and Windows (CRLF) line endings
    export $(grep -v '^#' .env | tr -d '\r' | xargs)
else
    echo "‚ùå .env file not found!"
    exit 1
fi

# Check required variables
if [ -z "$AZURE_VISION_ENDPOINT" ]; then
    echo "‚ùå AZURE_VISION_ENDPOINT not set in .env"
    exit 1
fi

if [ -z "$AZURE_VISION_API_KEY" ]; then
    echo "‚ùå AZURE_VISION_API_KEY not set in .env"
    exit 1
fi

if [ "$AZURE_VISION_ENABLED" != "true" ]; then
    echo "‚ö†Ô∏è  Azure Vision is disabled (AZURE_VISION_ENABLED != true)"
    echo "üí° Set AZURE_VISION_ENABLED=true in .env to enable testing"
    exit 1
fi

echo "üìç Endpoint: ${AZURE_VISION_ENDPOINT:0:50}..."
echo "üîë API Key: Present (${#AZURE_VISION_API_KEY} characters)"
echo "üéõÔ∏è  Enabled: $AZURE_VISION_ENABLED"
echo

# Create a test image (50x50 PNG - minimum size for Azure Vision)
TEST_IMAGE="/tmp/test_image.png"
echo "üñºÔ∏è  Creating test image..."

# Create a simple 50x50 blue PNG using base64
# This is a valid 50x50 blue square PNG
cat << 'EOF' | base64 -d > "$TEST_IMAGE"

EOF

if [ ! -f "$TEST_IMAGE" ]; then
    echo "‚ùå Failed to create test image"
    exit 1
fi

echo "‚úÖ Test image created: $(wc -c < "$TEST_IMAGE") bytes"

# Build API URL - using only 'tags' which is supported in all regions
API_URL="${AZURE_VISION_ENDPOINT}/computervision/imageanalysis:analyze?api-version=2024-02-01&features=tags&language=en"

echo "üì° Testing API endpoint..."
echo "üåê URL: $API_URL"
echo

# Make the API call
echo "üì§ Sending request to Azure Vision API..."
RESPONSE=$(curl -s -w "HTTP_CODE:%{http_code}" \
    -X POST \
    -H "Ocp-Apim-Subscription-Key: $AZURE_VISION_API_KEY" \
    -H "Content-Type: application/octet-stream" \
    --data-binary "@$TEST_IMAGE" \
    "$API_URL")

# Extract HTTP code and response body
HTTP_CODE=$(echo "$RESPONSE" | grep -o "HTTP_CODE:[0-9]*" | cut -d: -f2)
RESPONSE_BODY=$(echo "$RESPONSE" | sed 's/HTTP_CODE:[0-9]*$//')

echo "üì• Response Status: $HTTP_CODE"

if [ "$HTTP_CODE" = "200" ]; then
    echo "üéâ SUCCESS! Azure Vision API is working!"
    echo
    echo "üìä API Response:"
    echo "$RESPONSE_BODY" | jq '.' 2>/dev/null || echo "$RESPONSE_BODY"
    
    # Try to extract key information
    echo
    echo "üìã Summary:"
    MODEL_VERSION=$(echo "$RESPONSE_BODY" | jq -r '.modelVersion // "Unknown"' 2>/dev/null)
    TAG_COUNT=$(echo "$RESPONSE_BODY" | jq -r '.tagsResult.values | length // 0' 2>/dev/null)
    FIRST_TAG=$(echo "$RESPONSE_BODY" | jq -r '.tagsResult.values[0].name // "No tags"' 2>/dev/null)
    FIRST_TAG_CONF=$(echo "$RESPONSE_BODY" | jq -r '.tagsResult.values[0].confidence // "N/A"' 2>/dev/null)
    
    echo "   ü§ñ Model Version: $MODEL_VERSION"
    echo "   üè∑Ô∏è  Tags Found: $TAG_COUNT"
    echo "   üîù Top Tag: $FIRST_TAG (confidence: $FIRST_TAG_CONF)"
    
elif [ "$HTTP_CODE" = "401" ]; then
    echo "‚ùå Authentication Error (401 Unauthorized)"
    echo "üí° Troubleshooting:"
    echo "   ‚Ä¢ Check your API key in .env file"
    echo "   ‚Ä¢ Verify the key is active in Azure Portal"
    echo "   ‚Ä¢ Try using Key 2 instead of Key 1"
    echo
    echo "Response:"
    echo "$RESPONSE_BODY"
    
elif [ "$HTTP_CODE" = "404" ]; then
    echo "‚ùå Endpoint Error (404 Not Found)"
    echo "üí° Troubleshooting:"
    echo "   ‚Ä¢ Check your endpoint URL in .env file"
    echo "   ‚Ä¢ Ensure the resource name is correct"
    echo "   ‚Ä¢ Verify the resource is active in Azure Portal"
    echo
    echo "Response:"
    echo "$RESPONSE_BODY"
    
else
    echo "‚ùå API Error (HTTP $HTTP_CODE)"
    echo "Response:"
    echo "$RESPONSE_BODY"
fi

# Cleanup
rm -f "$TEST_IMAGE"

echo
echo "üîç Test completed!"